// AC Navigator page script
function AppendQuestionList()
{
	// All questions stored in this array
	var questionsArray = ['formuliertschlussige ziele und zeige auf, wie diesen zu erriechen sind 1',
					 'formuliertschlussige ziele und zeige auf, wie diesen zu erriechen sind 2',
					 'formuliertschlussige ziele und zeige auf, wie diesen zu erriechen sind 3',
					 'formuliertschlussige ziele und zeige auf, wie diesen zu erriechen sind 4',
	                 'formuliertschlussige ziele und zeige auf, wie diesen zu erriechen sind 5'];

	// Number of buttons in a question
	var numberOfButtons = 5;

	for(var i = 0;i < questionsArray.length; i++)
	{

		var listHtml = '<li class="list-group-item list-group-item-action">\
										<div class="row">\
										<p class="col-md-7">'+ questionsArray[i]
										+'</p>\
										<div class="col-md-5">';
		for(var j = 1;j <= numberOfButtons; j++)
		{
			listHtml += '<button type="button" id="${i}" data-toggle="button" aria-pressed="false" class="btn btn-warning btn-margin rounded-circle font-weight-bold">'+ j +'</button>';
		}

		listHtml += '		</div>\
						</div>\
					</li>';
		$("#question-list").append(listHtml);
	}
}

