function increaseValue(id_num) {
  var value = parseInt(document.getElementById(id_num).value, 10);
  value = isNaN(value) ? 0 : value;
  value++;
  document.getElementById(id_num).value = value;
}

function decreaseValue(id_num) {
  var value = parseInt(document.getElementById(id_num).value, 10);
  value = isNaN(value) ? 0 : value;
  value < 1 ? value = 1 : '';
  value--;
  document.getElementById(id_num).value = value;
}
